mapper.select:
SELECT * FROM TR_MESSAGE
TR_EMAIL = #{email}
AND WRITER = 'Trainer'
AND WRITER = 'Member'
OR MEM_EMAIL = #{email}
AND WRITER = 'Trainer'
AND WRITER = 'Member'
ORDER BY WRITE_DATE DESC LIMIT ${paging.start}, ${paging.cntPerPage}

SELECT * FROM TR_MESSAGE
MEM_EMAIL = #{email}
AND WRITER = 'Member'
AND WRITER = 'Trainer'
OR TR_EMAIL = #{email}
AND WRITER = 'Member'
AND WRITER = 'Trainer'
ORDER BY WRITE_DATE DESC LIMIT ${paging.start}, ${paging.cntPerPage}
